import osimport requestsfrom bs4 import BeautifulSoup# URL manzilingizurl = "https://www.olx.uz/d/oz/obyavlenie/gentra-2023-avtomat-egadan-ID3SVdG.html"# Sahifani olishresponse = requests.get(url)soup = BeautifulSoup(response.text, 'lxml')# 'swiper-wrapper' klassiga ega bo'lgan divni topishswiper_wrapper = soup.find('div', class_='swiper-wrapper')# Rasm URL'larini olishimages = swiper_wrapper.find_all('img')# Rasmni saqlash uchun papka yaratishos.makedirs('images', exist_ok=True)# Har bir rasmni yuklab olishfor img in images:    img_url = img.get('src')  # Rasm manzili    if img_url:        # Rasmlarni to'g'ri formatda olish (to'liq URL bo'lishi kerak)        if img_url.startswith('//'):            img_url = 'https:' + img_url        # Rasmlarni yuklab olish        img_name = img_url.split("/")[-1]  # Rasmning nomini olish        img_path = os.path.join('images', img_name)        # Rasmni yuklab olish va saqlash        img_data = requests.get(img_url).content        with open(img_path, 'wb') as f:            f.write(img_data)        print(f"Rasm saqlandi: {img_name}")print("Barcha rasmlar yuklab olindi.")