import osimport requestsfrom bs4 import BeautifulSoupurl = "https://www.olx.uz/d/oz/obyavlenie/kia-sportage-2023-x-line-2-0l-ID3TEbk.html"response = requests.get(url)soup = BeautifulSoup(response.text, 'lxml')swiper_wrapper = soup.find('div', class_='swiper-wrapper')images = swiper_wrapper.find_all('img')os.makedirs('images', exist_ok=True)for img in images:    img_url = img.get('src')    if img_url:        if img_url.startswith('//'):            img_url = 'https:' + img_url        img_name = f'{img_url.split("/")[-2]}.jpg'        img_path = os.path.join('images', img_name)        img_data = requests.get(img_url).content        with open(img_path, 'wb') as f:            f.write(img_data)